import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./ArtistProfile.scss";
import axios from "axios";

function ArtistProfile () {
  const { idFromParams } = useParams();
  const [artists, setArtists] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const artistIds = ["7cuORKnBSeW5Ly71bTdbYX", "4OueFaBxSIJs5Ct59hbFyi", "4lv6oj1VnrNBaGkcgVcIx2", "1WcrdHWOBkddrermsQNQcX", "5LQvp46hs3IVV4GF5Fi0Z0"]
      const artistsData = [];
      for (const artistId of artistIds) {
        const result = await axios(`https://v1.nocodeapi.com/fyiah876/spotify/fHCqbcjtNZRlBCZl/artists?id=${artistId}`);
        artistsData.push(result.data);
      }
      setArtists(artistsData);
    };
    fetchData();
  }, []);

  if (artists.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div className="artist-profile">
      {artists.map(artist => (
        <div key={artist.id}>
          <img src={artist.images[0].url} alt={artist.name} />
          <h2>{artist.name}</h2>
          <p>Monthly Listeners: {artist.followers.total}</p>
          <p>Songs: {artist.total_tracks}</p>
          <p>Streams: {artist.popularity}</p>
          {/* Add contact info here */}
        </div>
      ))}
    </div>
  );
};

export default ArtistProfile;
