import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./ArtistProfile.scss";
import axios from "axios";

function ArtistProfile() {
  const { idFromParams } = useParams();
  const [artist, setArtist] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        `https://v1.nocodeapi.com/fyiah876/spotify/fHCqbcjtNZRlBCZl/artists?id=${idFromParams}&include_groups=album,single`
      );
      setArtist(result.data);
    };
    fetchData();
  }, [idFromParams]);

  if (!artist) {
    return <div>Loading...</div>;
  }

  return (
    <div className="artist-profile">
      <img src={artist.images[0].url} alt={artist.name} />
      <h2>{artist.name}</h2>
      <p>Monthly Listeners: {artist.followers.total}</p>
      <p>Songs: {artist.total_tracks}</p>
      <p>Streams: {artist.popularity}</p>
      <div>
        <h3>Top Tracks</h3>
        <ul>
          {artist.tracks.map((track) => (
            <li key={track.id}>
              {track.name} - {track.album.name}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default ArtistProfile;
