import React from "react";
import styled from "styled-components";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import IconButton from "@mui/material/IconButton";
import InputBase from "@mui/material/InputBase";
import Avatar from "@mui/material/Avatar";
import { alpha, styled as muiStyled } from "@mui/material/styles";
import { Search as SearchIcon } from "@mui/icons-material";
import { AccountCircle as AccountCircleIcon } from "@mui/icons-material";

const theme = createTheme({
  palette: {
    primary: {
      main: "#1DB954",
    },
  },
});

const Search = muiStyled("div")(({ theme }) => ({
  position: "relative",
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  "&:hover": {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginRight: theme.spacing(2),
  marginLeft: 0,
  width: "100%",
  [theme.breakpoints.up("sm")]: {
    marginLeft: theme.spacing(3),
    width: "auto",
  },
}));

const SearchIconWrapper = muiStyled("div")(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: "100%",
  position: "absolute",
  pointerEvents: "none",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
}));

const NavItem = styled.a`
  text-decoration: none;
  color: white;
  margin: 0 10px;
  &:hover {
    text-decoration: underline;
  }
`;

function Navbar({ navBackground }) {
  const userInfo = {
    name: "John Doe",
    userUrl: "/user/1",
  };

  return (
    <AppBar
      position="sticky"
      sx={{
        backgroundColor: navBackground ? "rgba(0,0,0,0.7)" : "transparent",
        transition: "0.3s ease-in-out",
      }}
    >
      <Toolbar>
        <div>
          <NavItem href="/">Dashboard</NavItem>
          <NavItem href="/artists">Artists</NavItem>
          <NavItem href="/albums">Albums</NavItem>
          <NavItem href="/playlists">Playlists</NavItem>
        </div>
        <Search>
          <SearchIconWrapper>
            <SearchIcon />
          </SearchIconWrapper>
          <InputBase
            placeholder="Search for songs, artists, or playlists"
            inputProps={{ "aria-label": "search" }}
            sx={{ color: "inherit" }}
          />
        </Search>
        <div>
          <NavItem href={userInfo.userUrl}>
            <Avatar>
              <AccountCircleIcon />
            </Avatar>
            <span>{userInfo.name}</span>
          </NavItem>
        </div>
      </Toolbar>
    </AppBar>
  );
}

export default function App() {
  return (
    <ThemeProvider theme={theme}>
      <Navbar />
      {/* rest of your app code */}
    </ThemeProvider>
  );
}
